import { Component, OnInit } from '@angular/core';
import axios from 'axios';
import { Todo } from '../../models/Todo';

@Component({
  selector: 'app-todos',
  templateUrl: './todos.component.html',
  styleUrls: ['./todos.component.css'],
})
export class TodosComponent implements OnInit {
  todos: Todo[] = [];

  inputTodo: string = '';

  constructor() {}

  async ngOnInit() {
    this.todos = (await axios.get('http://localhost:8000/', {
      headers: {
        'X-CSRFTOKEN':
          '3zf2mziv3lrY1zjcCpcV55KZLQGUUJliYdcCY646oqnBxGSkXhFAQcw3znZhT6tA',
      },
    })).data;
  }
  toggleDone(id: number) {
    this.todos.map((v, i) => {
      if (i == id) v.Completed = !v.Completed;
      return v;
    });
    const todo = this.todos.find(item => item.id === id);
    axios.put ("http://localhost:8000/" + id, {
      ...todo,
      Completed: tru
    })
  }
  deleteTodo(id: Number | undefined) {
    this.todos = this.todos.filter((todo, i) => todo.id !== id);
    axios.delete('http://localhost:8000/delete/' + id, {
      headers: {
        'X-CSRFTOKEN':
          '3zf2mziv3lrY1zjcCpcV55KZLQGUUJliYdcCY646oqnBxGSkXhFAQcw3znZhT6tA',
      },
    });
  }

  addTodo() {
    this.todos.push({
      id: undefined,
      Title: this.inputTodo,
      Completed: false,
    });
    axios.post(
      'http://localhost:8000/create',
      {
        Title: this.inputTodo,
        Description: '',
        Date: '2000-01-01',
      },
      {
        headers: {
          'X-CSRFTOKEN':
            '3zf2mziv3lrY1zjcCpcV55KZLQGUUJliYdcCY646oqnBxGSkXhFAQcw3znZhT6tA',
        },
      }
    );
    this.inputTodo = '';
  }
}
<div class ="todos">
    <form (submit) = "addTodo()">
        <input
        type="text"
        name="inputTodo"
        placeholder=" - Enter todo..."
        class="todo-input"
        [(ngModel)]="inputTodo"
        />
        <input type="submit" value="Add Todo" class="todo-submit">
    </form>

    <div  *ngFor="let todo of todos; let i = index;"class = "todo {{(todo.Completed ?'done': '') }}">
        <div class="id">{{ i }}</div>
        <div class="content" (click)="toggleDone(i)">{{ todo.Title }}</div>
        <button class = "delete" (click)="deleteTodo(todo.id)">Remove</button>
    </div>
</div>
